void ve(int x, int y, int mina, int maxa, int minb, int maxb, int mang[][100]) {
	for (int i = mina; i < maxa; i += 2) {
		GotoXY(x, y + (i - mina) / 2);
		for (int j = minb; j < maxb; j++) {
			SetColor(mang[i + 1][j], mang[i][j]);
			cout << u8"▄";
		}
		cout << endl;
	}
}
